import { HttpClient } from '@angular/common/http';
import { OnInit } from '@angular/core';
import { Component } from '@angular/core';
import { Customer } from '../summary/summary.component';
import { environment } from 'src/environments/environment';
import { UsageService } from '../services/usage.service';
import { UsageByCustomerDTO } from '../models/Usage';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {

  usageByCustomer: UsageByCustomerDTO[] = [];

  constructor(
    private readonly http: HttpClient,
    private readonly usageService: UsageService) { }

  ngOnInit(): void {
    this.http.get(`${environment.baseUrl}/api/health/check`)
      .subscribe();

    const fromDate = new Date(2023, 1, 1);
    const toDate = new Date(2025, 1, 1);

    this.usageService.getUsagesGroupByCustomer(fromDate, toDate)
      .subscribe(data => this.usageByCustomer = data);

    // const customerId = "";
    // this.usageService.getUsagesGroupBySim(customerId, fromDate, toDate)
    //   .subscribe(
    //     data => {
    //       // Handle grouped data by SIM
    //       this.usages = data;
    //     },
    //     error => {
    //       this.error = error.message; // Handle the error (optional)
    //     }
    //   );
  }

  onTopCustomerClick(customer: Customer) {
    alert('onTopCustomerClick');
  }

  onRefresh() {
    alert('onRefresh');
  }
}
