import { OnInit } from '@angular/core';
import { Component } from '@angular/core';
import { UsageService } from '../services/usage.service';
import { UsageByCustomerDTO } from '../models/Usage';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  usageByCustomer: UsageByCustomerDTO[] = [];
  simsCount: number = 0;
  totalUsage: number = 0;

  fromDate: Date = new Date(2023, 1, 1);
  toDate: Date = new Date(2025, 1, 1);

  constructor(private readonly usageService: UsageService) { }

  ngOnInit(): void {
    this.usageService.getUsagesGroupByCustomer(this.fromDate, this.toDate)
      .subscribe(data => {
        let sum = 0;
        data.forEach((el) => sum += el.simCount);
        this.simsCount = sum;

        this.usageByCustomer = data;
      });
  }

  onTopCustomerClick(customer: UsageByCustomerDTO) {
    // const customerId = "";
    // this.usageService.getUsagesGroupBySim(customerId, fromDate, toDate)
    //   .subscribe(
    //     data => {
    //       // Handle grouped data by SIM
    //       this.usages = data;
    //     },
    //     error => {
    //       this.error = error.message; // Handle the error (optional)
    //     }
    //   );
    alert('onTopCustomerClick');
  }

  onRefresh() {
    alert('onRefresh');
  }
}
